import numpy as np

def calculate_balance(sequence):
    d = np.sum(sequence)
    return len(sequence) - 2 * d

def left_circular_shift(seq, shift_amount):

    n = len(seq)
    # Use modulo to handle shifts larger than sequence length
    shift = shift_amount % n
    # Rotate by slicing and concatenating
    shifted_seq = seq[shift:] + seq[:shift]
    return shifted_seq

def compute_difference(sequence):
    N = len(sequence)
    results = []
    for k in range(N):
        shifted_sequence = np.roll(sequence, k)
        agreements = np.sum(sequence == shifted_sequence)
        disagreements = N - agreements
        result = agreements - disagreements
        results.append(result)
    return results

def acr(seq1,seq2):
    N = len(seq2)
    n = len(seq1+seq2)
    results = []
    for k in range(N):
        shifted = np.roll(seq2,k)
        sequence = seq1.extend(shifted.tolist())
        unique_vals = compute_difference(sequence)
        unique_vals = [abs(val) for val in unique_vals]
        acr_max = max(set(unique_vals[1:])) if len(unique_vals) > 1 else 0
        results.append(int(acr_max))

    return results


def compute_acr(binary_sequence):

    unique_vals = compute_difference(binary_sequence)
    unique_vals = [abs(val) for val in unique_vals]
    acr_max = max(set(unique_vals[1:])) if len(unique_vals) > 1 else 0

    return acr_max

