import ast
import json

import numpy as np
from multiprocessing import Pool, cpu_count
from Legendre_sequence_generator import legendre_binary_sequence


def read_numbers():
    list_of_combinations = []
    with open('combinations.txt') as f:
        for line in f:
            line = line.strip()
            if not line:
                continue
            parts = line.split(',')
            num1, num2 = map(int, parts)
            small, large = min(num1, num2), max(num1, num2)
            list_of_combinations.append([small, large])
            list_of_combinations.append([large, small])
    return list_of_combinations


def flip_bits_list(bit_list):
    return [1 - bit for bit in bit_list]


def left_circular_shift(seq, shift_amount):
    n = len(seq)
    shift = shift_amount % n
    shifted_seq = seq[shift:] + seq[:shift]
    return shifted_seq


def compute_difference(sequence):
    N = len(sequence)
    results = []
    for k in range(N):
        shifted_sequence = np.roll(sequence, k)
        agreements = np.sum(sequence == shifted_sequence)
        disagreements = N - agreements
        result = agreements - disagreements
        results.append(result)
    return results


def compute_acr(binary_sequence):
    unique_vals = compute_difference(binary_sequence)
    unique_vals = [abs(val) for val in unique_vals]
    acr_max = max(set(unique_vals[1:])) if len(unique_vals) > 1 else 0
    return acr_max


def process_single_task(args):
    """
    Worker function for multiprocessing.
    args: (numbers, shift_k)
    """
    numbers, k = args
    seq1 = legendre_binary_sequence(numbers[0])
    seq2 = legendre_binary_sequence(numbers[1])
    flipped_sequence = flip_bits_list(seq1)
    shifted = left_circular_shift(flipped_sequence, k)
    concatenated = seq2 + shifted
    acr = compute_acr(concatenated)
    return {'sequence': concatenated, 'combination': [numbers[0], numbers[1]], 'ACR': int(acr), 'shift': k}


if __name__ == "__main__":
    combination_of_1500 = read_numbers()

    # Prepare list of tasks: tuples of (numbers, k)
    tasks = []
    # Use first 2 pairs as in your original code; you can adjust range or entire list
    for numbers in combination_of_1500:
        seq1 = legendre_binary_sequence(numbers[0])
        flipped_len = len(seq1)
        for k in range(flipped_len):
            tasks.append((numbers, k))

    with Pool(processes=cpu_count()) as pool:
        data = pool.map(process_single_task, tasks)

    print(data)

    with open("output_data_1500.txt", "w") as f:
        for entry in data:
            f.write(str(entry) + "\n")

    print("Data saved to output_data_1500.txt")

    min_value = min(item['ACR'] for item in data)
    min_entries = [item for item in data if item['ACR'] == min_value]

    print(f"Minimum ACR is: {min_value}")
    print("Entries with minimum value:")
    for entry in min_entries:
        print(f"Sequence: {entry['sequence']}, Combination: {entry['combination']}, ACR: {entry['ACR']}, Shift: {entry['shift']}")



